using System;

public class Program
{
  public static void Main(string[] args)
  {
    // Numeric Types.
    int age = 18;
    float pi = 3.1415926f;
    double e = 2.718281828459045;

    // Alphanumeric Types.
    char firstLetter = 'a';
    string name = "Joseph";

    // Logical Types.
    bool isEven = true;
  }
}

